export function assertNotComplex(
    tensor: TensorInfo|TensorInfo[], opName: string): void {
  if (!Array.isArray(tensor)) {
    tensor = [tensor];
  }
  tensor.forEach(t => {
    if (t != null) {
      assert(
          t.dtype !== 'complex64',
          () => `${opName} does not support complex64 tensors.`);
    }
  });
}
